countChar :: Char -> [Char] -> Int
countChar c = length . filter (== c)

currentFloor :: [Char] -> Int
currentFloor input = countChar '(' input - countChar ')' input

tuplize :: ((a -> b), (a -> c)) -> a -> (b, c)
tuplize (f, g) x = (f x, g x)

visit = tuplize (length, currentFloor)
allVisits = map visit $ inits input

visitedBasement :: (Int, Int) -> Bool
visitedBasement (_, f) = f == -1

indexVisitedBasement = case find visitedBasement allVisits of
                         Just (i, _) -> i
                         Nothing     -> -1

main = do putStrLn "Sum of Valid Room Sectors:"
          print $ currentFloor input
          putStrLn ""
          putStrLn "Character Position where we enter Basement:"
          print $ indexVisitedBasement
